package ar.com.ucema.ia.chess.iama;import java.util.List;import aima.search.framework.HeuristicFunction;import ar.com.ucema.ia.chess.model.ChessBoard;import ar.com.ucema.ia.chess.model.ChessGame;import ar.com.ucema.ia.chess.model.pieces.ChessPiece;/** * Esta heuristica define un valor para cada pieza. * @author matiassuarez * */public class ChessHeuristicFunction implements HeuristicFunction {	public double getHeuristicValue(Object arg0) {		ChessBoard board = (ChessBoard) arg0;		List<ChessPiece> list = board.getChessCellsWithColor(ChessGame.getInstance().getColorToPlayNext());		int total = 0;				for (ChessPiece chessPiece : list) {			total += chessPiece.getPieceValue();		}				return total;	}}