package ar.com.ucema.ia.chess.game;import junit.framework.TestCase;import ar.com.ucema.ia.chess.model.ChessBoard;import ar.com.ucema.ia.chess.model.ChessCell;import ar.com.ucema.ia.chess.model.ChessColumns;import ar.com.ucema.ia.chess.model.WhitePieceChessMovement;import ar.com.ucema.ia.chess.model.pieces.ChessPiece;import ar.com.ucema.ia.chess.model.pieces.Pawn;public class GeneralMovementTestCase extends TestCase {				public void testEatingAPawn() {		ChessBoard board = new ChessBoard();		Pawn blackPawn = Pawn.createBlackPawn();		ChessPiece whitePawn = board.getChessCellAt(ChessColumns.B,2).getPiece();		board.setPieceAt(ChessColumns.A, 3, blackPawn);		whitePawn.move(new WhitePieceChessMovement(new ChessCell(ChessColumns.B,2), new ChessCell(ChessColumns.A,3), board));				assertEquals(whitePawn, board.getChessCellAt(ChessColumns.A, 3).getPiece());	}	}